import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlayCircle } from "@fortawesome/free-solid-svg-icons";
import LanguageDropdown from "../Dropdowns/LanguageDropdown";
import ThemeDropdown from "../Dropdowns/ThemeDropdown";
import { checkStatus, submitCode } from "../";

const Navbar = ({
	language,
	setLanguage,
	setTheme,
	theme,
	setOutput,
	setStatus,
	testInput,
	code
}) => {
	const handleSubmit = async () => {
		setStatus("Running");
		const formData={
			language_id: language.id,
      source_code: btoa(code),
      stdin: btoa(testInput),
		}

    try {
      const { data } = await submitCode(formData)
      const { token } = data;
      const {data: output, success, err}  = await checkStatus(token);

      if(success) {
        setOutput(output);
        setStatus("Finished");
      }else{
        console.log(err);
        setStatus("Error");
      }
    }
    catch(err){
      let error = err.response ? err.response.data : err;
      console.log(error);
    }
	};

	return (
		<div className="grid grid-cols-2 m-2">
			<button
				onClick={handleSubmit}
				className="bg-[#5cb85c] border-[#4cae4c] border-1 text-white rounded-full w-32 text-sm md:text-base hover:border-[#398439] hover:bg-[#449d44] ">
				<FontAwesomeIcon
					icon={faPlayCircle}
					className="mr-2"
					color="white"
					size="sm"
				/>
				<span>Run Code</span>
			</button>
			<div className="grid  grid-cols-2 gap-2">
				<LanguageDropdown language={language} setLanguage={setLanguage}/>
				<ThemeDropdown theme={theme} setTheme={setTheme} />
			</div>
		</div>
	);
};

export default Navbar;
